$(document).ready((function(){document.querySelectorAll('a[href^="#"').forEach(e=>{e.addEventListener("click",(function(e){e.preventDefault();let t=this.getAttribute("href").substring(1);const o=document.getElementById(t),l=document.querySelector(".scrollto").offsetHeight,r=o.getBoundingClientRect().top-l;window.scrollBy({top:r-70,behavior:"smooth"})}))}),$(window).on("scroll",(function(){var e=$(this).scrollTop();e>107&&$("header").addClass("scrolled"),e<=107&&$("header").removeClass("scrolled")}))}));